mapper.select:
select f.*, nvl2(h.user_idx,1,0) as islike, (select COUNT(*) from feed_heart where feed_heart.FEED_IDX = f.FEED_IDX)as heart from feed f left JOIN FEED_HEART h on f.feed_idx = h.feed_idx and h.user_idx = #{user_idx}
REGEXP_LIKE(replace(f.contents,' ',''), #{keyword}) OR REGEXP_LIKE(replace(f.title,' ',''), #{keyword})
order by f.regdate desc

select COUNT(*)
from feed_heart
where feed_idx = #{feed_idx}

select COUNT(*) from feed_heart
where user_idx = #{user_idx}
and feed_idx = #{feed_idx}

mapper.update:
update feed set
title = #{title}, contents = #{contents} where feed_idx =
#{feed_idx}
